var shelpers = require('shelpers');
var sh = require('shelpers').shelpers;

function showMethod(hop) {
    hop = sh.dv(hop, 1)
    var e = new Error('Error Msg')
    console.log('e', e.stack)
    var str = e.stack;
    var lines = str.split('\n')
    function trim(x){
        return x.trim();
    }
    var lines = lines.map(trim)
    var fxName = lines[1+hop]
    fxName = sh.str.after(fxName, 'at ')
    fxName = sh.str.before(fxName, ' (')
    var line = sh.str.after(fxName, ' ')
    console.log('fxName', fxName)
    return fxName;
}

function testCallingMethod() {
    showMethod(1)
}
testCallingMethod()